export type StaticPublishRc = {
  // Name of KV Store to use.
  kvStoreName: string,

  // Publish ID. This is a unique string "prefix" that identifies your assets in the
  // KV Store. This is generated by init-app and is usually not to be changed.
  publishId: string;

  // Default collection name.
  defaultCollectionName: string,
}
